var config    = require('../config');
var sequelize = require('sequelize');

if(config.orm && config.orm.connection){
  var db = new sequelize(config.orm.connection);
}

var orm = function(self){
  if(db){
    return {
      db: db,
      user: require('../schema/user').apply(null, [db, sequelize])
    }
  }
  else {
    return false;
  }
};

module.exports = function(self){
  return new orm(self);
};
