module.exports = function(db, sqlize){

  return db.define('user', {
    id: {
      type: sqlize.UUID,
      defaultValue: sqlize.UUIDV4,
      primaryKey: true,
      allowNull: false
    },
    email: {
      allowNull: false,
      unique: true,
      type: sqlize.STRING
    },
    password: {
      allowNull: false,
      type: sqlize.STRING
    },
    first_name: {
      allowNull: false,
      type: sqlize.STRING,
    },
    last_name: {
      allowNull: false,
      type: sqlize.STRING,
    }
  },
  {
    tableName: 'user',
    timestamps: false
  });

};
