#!/usr/bin/env node
require('shelljs/global');

exec('git clone https://github.com/erikuix/restrap.git .');

rm('-rf', '.git');
rm('-rf', 'bin');
rm('-rf', 'node_modules');
rm(['main.js', 'package.json', 'README.md']);

exec('mv base/* ./');
exec('npm install');

console.log('Restrap installed successfully.');
process.exit();
